import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
//import { useNavigate, NavLink } from "react-router-dom";
import { useGlobalContext } from "../../context/globalContext";
const SignUp = () => {
  //const navigate = useNavigate();
  
  const {isSubmit, setIsSubmit} = useGlobalContext;
  const[formErrors, setFormErrors] = useState({});

  const [user, setUserDetails] = useState({
    username: "",
    email: "",
    password: "",
    cpassword: "",
  });

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setUserDetails({
      ...user,
      [name]: value,
    });
  };

  const validateForm = (values) => {
    const error = {};
    const regex = /^[^\s+@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.username) {
      error.fname = "First Name is required";
    }
    if (!values.email) {
      error.email = "Email is required";
    } else if (!regex.test(values.email)) {
      error.email = "This is not a valid email format!";
    }
    if (!values.password) {
      error.password = "Password is required";
    } else if (values.password.length < 4) {
      error.password = "Password must be more than 4 characters";
    } else if (values.password.length > 10) {
      error.password = "Password cannot exceed more than 10 characters";
    }
    if (!values.cpassword) {
      error.cpassword = "Confirm Password is required";
    } else if (values.cpassword !== values.password) {
      error.cpassword = "Confirm password and password should be same";
    }
    return error;
  };
  const signupHandler = (e) => {
    e.preventDefault();
    setFormErrors(validateForm(user));
    setIsSubmit(true);
    // if (!formErrors) {
    //   setIsSubmit(true);
    // }
  };

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(user);
    //   axios.post("http://localhost:9002/signup/", user).then((res) => {
    //     alert(res.data.message);
    //     navigate("/login", { replace: true });
    //   });
    }
  }, [formErrors]);
  return (
    <SignUpStyled>
      <div className="register">
        <form>
          <h1>Create your account</h1>
          <input
            type="text"
            name="username"
            id="fname"
            placeholder="Username"
            onChange={changeHandler}
            value={user.username}
          />
          <p className="error">{formErrors.username}</p>
          <input
            type="email"
            name="email"
            id="email"
            placeholder="Email"
            onChange={changeHandler}
            value={user.email}
          />
          <p className="error">{formErrors.email}</p>
          <input
            type="password"
            name="password"
            id="password"
            placeholder="Password"
            onChange={changeHandler}
            value={user.password}
          />
          <p className="error">{formErrors.password}</p>
          <input
            type="password"
            name="cpassword"
            id="cpassword"
            placeholder="Confirm Password"
            onChange={changeHandler}
            value={user.cpassword}
          />
          <p className="error">{formErrors.cpassword}</p>
          <button className="button_common" onClick={signupHandler}>
            Register
          </button>
        </form>
        {/* <NavLink to="/login">Already registered? Login</NavLink> */}
      </div>
    </SignUpStyled>
  );
};

const SignUpStyled = styled.nav`
    .register {
    display: flex;
    justify-content:center;
    width: 450px;
    height:550px;
    background: #fff;
    border: 1px solid #dddfe2;
    box-shadow: 0 2px 4px rgb(0 0 0 / 10%), 0 8px 16px rgb(0 0 0 / 10%);
    border-radius: 8px;
    padding: 1rem;
    align-items: center;
    text-align: center;

    input {
    border-radius: 20px;
    border: 2px solid lightgrey;
    outline: none;
    color: #1d2129;
    margin: 2% 0;
    width: 90%;
    padding: 12px;
    font-size: 16px;
   }

   .button_common {
    background-color: olivedrab;
    color: white;
    padding: 15px 30px;
    border: none;
    font-size: 22px;
    border-radius: 15px;
    margin: 1rem;
    width: 90%;
}

.error {
    color: red;
    text-align: left;
    margin: auto;
    font-size: 16px;
    padding: 0px 1rem;
}

}`;
export default SignUp;
